import { NavSupport } from "./nav-support";
import { NavApps } from "./nav-apps";
import { NavManagement } from "./nav-management";

const data = {
  user: {
    name: "shadcn",
    email: "m@example.com",
    avatar: "/avatars/shadcn.jpg",
  },
  navMain: [
    {
      title: "Home",
