      icon: Calendar,
    },
    {
      title: "Team",
      url: "#",
      icon: Users,
    },
    {
      title: "Docs",
      url: "#",
      icon: FileText,
    },
    {
      title: "Automations",
      url: "#",
      icon: Zap,
    },
    {
      title: "Reporting",
      url: "#",
      icon: ChartNoAxesCombined,
