"use client"

import { type LucideIcon } from "lucide-react"

import {
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "@/components/ui/sidebar"

export function NavMain({
  items,
}: {
  items: {
    title: string
    url: string
    icon: LucideIcon
    isActive?: boolean
  }[]
}) {
  return (
    <SidebarGroup>
      <SidebarGroupLabel className="-ml-2 font-medium text-neutral-700">Essentials</SidebarGroupLabel>
      <SidebarMenu>
        {items.map((item) => (
            <SidebarMenuItem key={item.title}>
              <SidebarMenuButton asChild tooltip={item.title} className="text-neutral-500 text-xs h-8 font-medium data-[active=true]:border-neutral-300 hover:border hover:border-neutral-300 data-[active=true]:text-neutral-600 hover:text-neutral-600">
                <a href={item.url}>
                  <item.icon className="!size-4" />
                  <span className="tracking-tight">{item.title}</span>
                </a>
              </SidebarMenuButton>
            </SidebarMenuItem>
        ))}
      </SidebarMenu>
    </SidebarGroup>
  )
}

"use client"

import {
  ChevronRight,
  GripVertical,
  Plus,
  type LucideIcon,
} from "lucide-react"

import {
  SidebarGroup,
  SidebarGroupLabel,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
} from "@/components/ui/sidebar"
import {
  Collapsible,
  CollapsibleContent,
  CollapsibleTrigger,
} from "@/components/ui/collapsible"
import { usePathname } from "next/navigation"

export function NavSupport({
  support,
}: {
  support: {
    name: string
    url: string
    icon: LucideIcon
  }[]
}) {

  const pathname = usePathname();

  return (
    <Collapsible defaultOpen className="group/collapsible px-2">
      <SidebarGroup className="m-0 !px-0 border-t-[0.5px] border-neutral-200 py-2">
        <SidebarGroupLabel asChild className=" font-medium text-neutral-700 !px-0">
          <div className="flex items-center justify-between w-full">
            <CollapsibleTrigger className="flex items-center gap-1 w-full p-0">
              <ChevronRight className="size-4 transition-transform group-data-[state=open]/collapsible:rotate-90" />
              <span>Support</span>
            </CollapsibleTrigger>
            <div className="flex items-center gap-2">
              <Plus className="size-3.5 text-neutral-500 hover:text-neutral-700 cursor-pointer" />
              <GripVertical className="size-3.5 text-neutral-500 hover:text-neutral-700 cursor-pointer" />
            </div>
          </div>
        </SidebarGroupLabel>
        <CollapsibleContent className="">
          <SidebarMenu>
            {support.map((item, index) => (
                <SidebarMenuItem key={item.name}>
                  <SidebarMenuButton asChild tooltip={item.name} isActive={pathname === item.url} className="text-neutral-500 text-xs h-8 font-medium data-[active=true]:border data-[active=true]:border-neutral-300 data-[active=true]:text-neutral-600 hover:border hover:border-neutral-300 hover:text-neutral-600">
                    <a href={item.url}>
                      <item.icon className="" />
                      <span className="tracking-tight">{item.name}</span>
                    </a>
                  </SidebarMenuButton>
                </SidebarMenuItem>
            ))}
          </SidebarMenu>
        </CollapsibleContent>
      </SidebarGroup>
    </Collapsible>
  )
}

"use client"

